mapper.select:
-- 재귀 쿼리 사용 위한 임시 테이블 CategoryTree WITH RECURSIVE CategoryTree AS ( -- 특정 카테고리 조회 SELECT cno, name, level, parent_idx, anony_state, active_state, reg_date, reg_id, up_date, up_id FROM hanteo_board.category WHERE cno = #{cno} AND name = #{name}
UNION ALL
-- 자식 카테고리를 재귀적으로 선택 SELECT c.cno, c.name, c.level, c.parent_idx, c.anony_state, c.active_state, c.reg_date, c.reg_id, c.up_date, c.up_id FROM hanteo_board.category c -- 자식 카테고리 조회 가능하도록 parent_idx 및 cno 기준으로 조인 JOIN CategoryTree ct ON c.parent_idx = ct.cno ) -- 부모, 자식 관계를 나타내는 트리 구조 반환 SELECT * FROM CategoryTree
